# %%
current_floor = 0
first = True

for i,instruction in enumerate(puzzle_input):
    if instruction=="(":
        current_floor+=1
    elif instruction==")":
        current_floor-=1
    else:
        print(f"invalid instruction: {instruction}")
        sys.exit(1)
    if (current_floor==-1) and (first):
        print(i+1)
        first=False

